package cn.mcff.takecolor;

import javax.imageio.ImageIO;
import javax.swing.*;
import java.io.ByteArrayInputStream;
import java.util.Base64;

public class ImageBaseConfig {
    /*未选中的圆圈*/
    public static String circle = "";

    /*关闭按钮*/
    public static String off = "";

    /*设置图片*/
    public static ImageIcon setImage(String imageBaseConfig, int width, int height, int hints) {
        try {
            // 解码Base64字符串为字节数组
            byte[] imageBytes = Base64.getDecoder().decode(imageBaseConfig);
            ImageIcon imageIcon = new ImageIcon(ImageIO.read(new ByteArrayInputStream(imageBytes)));
            imageIcon.setImage(imageIcon.getImage().getScaledInstance(width, height, hints));
            return imageIcon;
        } catch (Exception e) {
            return null;
        }
    }

}
